mapper.sql:
SELECT distinct job.jobOpenNo, sup.supportDate, sup.status, job.title, mem.username, mem.userId, mem.count, usr.task FROM jobopenBasic job, support sup, member mem, userDetail usr, university univ, language lang
usr.userid = mem.userid and usr.userid = sup.userid and univ.jobopenno = job.jobopenno and usr.jobopenno = job.jobopenno and usr.jobopenno = lang.jobopenno and usr.userid = lang.userid and lang.testname = 'TOEIC'
AND job.offer LIKE '%' || #{career} || '%'
AND job.form LIKE '%' || #{employment} || '%'
AND mem.count=#{academiccareer}
AND univ.univscore >= #{credit}
AND lang.score >= #{language}
AND sup.status = #{status}
AND job.title = #{title}

select job.title, job.offer, sup.supportdate, mem.username, sup.pass, sup.emailsend, usr.task, sup.userid, sup.jobopenno, sup.passNo from jobopenBasic job, support sup, member mem, userDetail usr
mem.userid = sup.userid and job.jobopenno = sup.jobopenno and usr.userid = mem.userid and job.display = 1
AND sup.pass IN ( #{pass}, #{fail} )
AND sup.pass = #{pass}
AND sup.pass = #{fail}
AND job.title = #{title}

